//리다이렉트될 화면

import axios from 'axios'
import React, { useEffect } from 'react'

import { Navigate, useNavigate } from 'react-router-dom'

const Kakao = props => {
  const navigate = useNavigate()
  //인가 코드 받기
  let code = new URL(window.location.href).searchParams.get('code')
  let grant_type = 'authorization_code'
  let client_id = 'dcb8c3b23d720c296085cbdc9046ece9'
  const API_URL = 'http://localhost:5000'
  useEffect(() => {
    //인가 코드 전달
    const auth_code = async () => {
      try {
        const response = await axios({
          method: 'post',
          url: `${API_URL}/handle-auth-code`,
          data: { data: code },
        })
        console.log(response.data)
        const data = await response.json()
        const token = data.token
        const isNewUser = data.isNewUser
        console.log('token:', token)

        //토큰을 로컬 스토리지에 저장
        localStorage.setItem('user_token', token)
      } catch (error) {
        console.log(error)
      }
    }
    auth_code()
  }, [])
  //권한이 필요한 api 요청을 보낼때 이 토큰을 불러와서 authorization 헤더에 포함
  async function fetchData() {
    // 로컬 스토리지에서 토큰 가져오기
    const token = localStorage.getItem('user_token')

    try {
      const response = await fetch(
        'https://your-server.com/api/some-secured-data',
        {
          method: 'GET',
          headers: {
            Authorization: `Bearer ${token}`,
            'Content-Type': 'application/json',
          },
        }
      )

      const data = await response.json()
      console.log('Data from secured API:', data)
    } catch (error) {
      console.error('Error fetching data:', error)
    }
  }
  return <></>
}

export default Kakao
