import axios from 'axios'
import React from 'react'
import { useNavigate } from 'react-router-dom'
import Animation from './Animation'
const Login = () => {
  const navigate = useNavigate()
  const Rest_api_key = 'dcb8c3b23d720c296085cbdc9046ece9'
  const redirect_uri = 'http://localhost:3000/oauth'

  const kakaoURL = `http://kauth.kakao.com/oauth/authorize?client_id=${Rest_api_key}&redirect_uri=${redirect_uri}&response_type=code`
  const handleLogin = () => {
    window.location.href = kakaoURL

    const code = new URL(window.location.href).searchParams.get('code')
    //인가 코드 -> 엑세스 토큰을 얻을 수 있음. 카카오 api를 사용하여 사용자 정보를 가져오거나 다른 작업 수행 가능
    const kakaologin = async () => {
      axios(`http:// 143.248.194.51/oauth?code=${code}`, {
        method: 'GET',
        headers: { 'Content-Type': 'application/x-www-form-urlencoded' },
        body: `grant_type=authorization_code&client_id=${Rest_api_key}&redirect_url=${redirect_uri}&code=${code}}`,
      })
        .then(res => res.json())
        .then(data => {
          if (data.access.token) {
            localStorage.setItem('token', data.access_token)
          } else {
            window.alert('로그인에 실패했습니다.')
            navigate('/Login')
          }
        })
    }

    return (
      <>
        <button onClick={handleLogin}>카카오톡</button>
      </>
    )
  }
}
export default Login
