import React, { useRef, useEffect } from 'react'
import L from 'leaflet'
import 'leaflet/dist/leaflet.css'
import geoJSONData from './path/to/your/state-geojson-data.json'

const MapComponent = () => {
  const mapRef = useRef(null)

  useEffect(() => {
    const map = L.map(mapRef.current).setView([37.5326, 127.024612], 7)
    L.tileLayer('https://{s}.tile.openstreetmap.org/{z}/{x}/{y}.png').addTo(map)

    const stateStyle = {
      fillColor: 'white',
      weight: 2,
      opacity: 1,
      color: 'black',
      fillOpacity: 0.7,
    }

    const onEachFeature = (feature, layer) => {
      layer.on({
        click: e => {
          e.target.setStyle({ fillColor: 'blue' })
        },
      })
    }

    L.geoJSON(geoJSONData, {
      style: stateStyle,
      onEachFeature,
    }).addTo(map)

    return () => {
      map.remove()
    }
  }, [])

  return (
    <div id="map" style={{ height: '100vh', width: '100%' }} ref={mapRef}></div>
  )
}

export default MapComponent
